import React, { Component } from 'react'
import { Col, Image, Button, Form } from 'react-bootstrap'
import { Link, withRouter } from 'react-router-dom'
import { BsFillHeartFill, BsFillForwardFill } from "react-icons/bs";

const forget = {
    fontSize: "25px"


};
const notForget = {
    marginLeft: "0",
    fontSize: "25px"
};
class PostCard extends Component {

    state = {
        isSent: false
      };

      loginHandler = () => {
        //props from Login in App.js
        //this.state reps cred in App.js
        this.props.login(this.state);
      };
      componentDidMount(){
        console.log(window.location.host)
        this.setState({
            host: window.location.host
        })
    }

      changeHandler = (e) => {
        // console.log("name of field", e.target["name"]);
        // console.log("value of field", e.target.value);
        let temp = { ...this.state }; //copy state object
        temp[e.target.name] = e.target.value;
        this.setState(temp);
      };
      //http://localhost:5510/api/user/forget

      onSubmit = (e) => {
        e.preventDefault()
        Axios.post('http://localhost:5000/api/user/forget', {email: this.state.email, host: this.state.host})
          .then(res => {
            console.log(res)
            if (res.status == 200) {
              this.setState({
                isSent:true,
                message: res.data.message,
                color: "success"
              })

            } else {
              this.setState({
                isSent:true,
                message: res.data.message,
                color: "danger"
              })

            }

          })
          .catch(err => console.log(err))
      }

    render() {
        // let {firstname, lastname} = this.props.user
        let { title, poster, image, overview, content, _id } = this.props.post


        return (
            <Col md={5} className="m-2" style={{ borderStyle: "double", borderRadius: "20px", display: "flex", flexDirection: "column", alignItems: "center" }}>
                <Image roundedCircle style={{ height: 150, width: 250 }} src={image} ></Image>
                {/* <h5>{firstname} {lastname}</h5> */}
                <p>{title}</p>
                <h5>{poster}</h5>
                <h5>{overview}</h5>
                <h4>{content} </h4>
                <Form >
                <Button style={notForget} as={Link} to="/Favorite" variant="btn-outline-dark" md={6}>
                    <p><BsFillHeartFill /></p>
                </Button>
                <Button style={forget} as={Link} to="/Information" variant="btn-outline-dark" md={6}>
                    <p><BsFillForwardFill/></p>
                </Button>

                </Form>



            </Col>
        )
    }
}

export default withRouter(PostCard);
