import React, { useState, useEffect } from 'react'
import { Row, Form, Col, Button, Alert } from 'react-bootstrap'
import ''
// import Axios from 'axios'

const Register = (props) => {
    const [user, setUser] = useState({})// user info
    const [register, setRegister] = useState(false) // to show aleart
    let onChangeInput = ({ target: { name, value } }) => {
        setUser({ ...user, [name]: value })
    }
    // to add the user info to database
    // let onSubmit = (e) => {
    //     e.preventDefault()
    //     Axios.post('http://localhost:3000/Component/Register', user)
    //         .then(res => {
    //                 if (res.data.register){
    //                     props.history.push('/login')
    //            }else{
    //                     setRegister(true)
    //                     setTimeout(() => {
    //                         setRegister(false)
    //                     }, 4000);
    //                 } 
    //         })
    //         .catch(err => console.log(err))
    // }
    return (
        <div>
            <Form >
                <Row className="justify-content-center mt-5">
                    <Col md={{ span: 6, offset: 3 }}>
                        <Form.Row>
                            <Col md={6}>
                                <Form.Label className="form-group required control-label">First name</Form.Label>
                                <Form.Control placeholder="First name" name="firstName" />
                            </Col>
                        </Form.Row>
                        <Form.Row><br /></Form.Row>
                        <Form.Row>
                            <Col md={6}>
                                <Form.Label>Last name</Form.Label>
                                <Form.Control placeholder="Last name" name="lastName" />
                            </Col>

                        </Form.Row>
                        <Form.Row> <br /></Form.Row>
                        <Form.Row>
                            <Form.Group as={Col} md={6} controlId="formGridEmail">
                                <Form.Label>Email</Form.Label>
                                <Form.Control type="email" placeholder="Enter email" name="email" />
                            </Form.Group>
                        </Form.Row>
                        <Form.Row>
                            <Form.Group as={Col} md={6} controlId="formGridPassword">
                                <Form.Label>Password</Form.Label>
                                <Form.Control type="password" placeholder="Password" name="password" />
                            </Form.Group>
                        </Form.Row>
                        <Col md={{ span: 8, offset: 2 }}>
                            <Button variant="btn btn-outline-dark" md={6}>
                                Submit
                    </Button>
                        </Col>
                    </Col>
                </Row>
            </Form>
        </div>
    )
}

export default Register;
