import React, { Component } from 'react'
import { Container, Row, Col, Form } from 'react-bootstrap'
import PostCard from './PostCard'
import axios from "axios"
import Circle from 'react-animation-h3ro-yasir'

export default class Timeline extends Component {

    state = {
        Timeline: [],
        loading: false,
        selectedPost: []
    }

    componentDidMount() {

        axios.get('https://sei12.herokuapp.com/post/json')
            .then(res => {
                this.setState({
                    Timeline: res.data,
                    selectedPost: res.data,

                })
                setTimeout(() => {
                    this.setState({
                        loading : true
                    })
                }, 3000);
            }).catch(err => console.log(err))



    }

    filterPost = ({target:{value}})=>{

        if (value == "All")
        {
            this.setState({
                selectedPost : this.state.Timeline
            })
        }else{
            this.setState({
                selectedPost : this.state.Timeline.filter(post =>{

                    return post.typee == value
                }
                )
            })
        }
    }
    render() {
        console.log(this.state.Timeline)

        let Timeline = this.state.selectedPost.map(post => {

            if (post.link) return <PostCard post={post} />
        })
        let types = this.state.Timeline.map(post =>post.typee)
            types.unshift("All")
        return (
            <div>
                <Container className="mt-5">

                <Row className="mt-5 justify-content-center" >

                     {this.state.loading ?  Timeline : (<Circle />) }

                </Row>

                </Container>

            </div >
        )
    }
}
