import React, { Component } from 'react'
import { Col, Image, Button, Form } from 'react-bootstrap'
import { Link, withRouter } from 'react-router-dom'
import { BsFillHeartFill, BsFillTrashFill } from "react-icons/bs";
import moment from 'react-moment'
import Axios from 'axios'
const forget = {
    fontSize: "25px"


};
const notForget = {
    marginLeft: "0",
    fontSize: "25px"
};
class PostCard extends Component {



    render() {
        // let {firstname, lastname} = this.props.user
        let { title, poster, image, overview, content, createdAt} = this.props.post
        let time = moment(new Date(createdAt)).format("YYYY-MM-DD hh:mm:ss")


        return (
            <Col md={5} className="m-2" style={{ borderStyle: "double", borderRadius: "20px", display: "flex", flexDirection: "column", alignItems: "center" }}>
                <Image roundedCircle style={{ height: 150, width: 250 }} src={image} ></Image>
                {/* <h5>{firstname} {lastname}</h5> */}
                Created: {moment(new Date(createdAt)).format("YYYY-MM-DD hh:mm:ss")}
                <p>{title}</p>
                <h5>{poster}</h5>
                <h5>{overview}</h5>
                <h5>{content}</h5>

                <Form >
                    <Button style={notForget} as={Link} to="/Favorite" variant="btn-outline-dark" md={6}>
                        <p><BsFillHeartFill /></p>
                    </Button>
                    <Button style={forget} variant="btn-outline-dark" md={6}>
                        <p><BsFillTrashFill /></p>
                    </Button>

                </Form>



            </Col>
        )
    }
}

export default withRouter(PostCard);
