import React, { Component } from 'react'
import { Row, Form, Col, Button, Alert } from 'react-bootstrap'
import Axios from 'axios'
import '../App.css';
import { Link } from 'react-router-dom';
//import Axios from 'axios'

export default class CreatePost extends Component {

    constructor(props) {
        super(props);

        this.onFileChange = this.onFileChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            title: '',
            poster: '',
            image: '',
            overview: '',
            content: ''

        }
    }

    onFileChange(e) {
        this.setState({ 
            image: e.target.files[0] })
    }

    onSubmit(e) {
        e.preventDefault()
        const formData = new FormData()
        formData.append('image', this.state.image)
        Axios.post("http://localhost:5000/api/user-profile", formData, {
            //title, poster, overview, content
        }).then(res => {
            console.log(res)
        })
    }


    render() {
        return (
            <div>
            <Form >
                <Row className="justify-content-center mt-5">
                    <Col md={{ span: 6, offset: 3 }}>
                        <Form.Row>
                            <Col className="form-group required " md={6}>
                                <Form.Label className="control-label">Title</Form.Label>
                                <Form.Control placeholder="Title" name="title" onChange={(e) => onFileChange(e)}  />
                            </Col>
                        </Form.Row>
                        {/* <Form.Row><br /></Form.Row> */}
                        <Form.Row>
                            <Col className="form-group required " md={6}>
                                <Form.Label className="control-label">Poster</Form.Label>
                                <Form.Control placeholder="Poster" name="poster" onChange={() => onFileChange} />
                            </Col>

                        </Form.Row>
                        {/* <Form.Row><br /></Form.Row> */}
                        <Form.Row>
                            <Col className="form-group required " md={6}>
                                <Form.Label className="control-label">Image</Form.Label>
                                <Form.Control type="file" placeholder="image" name="image" onChange={() => onFileChange()} />
                            </Col>

                        </Form.Row>
                        {/* <Form.Row> <br /></Form.Row> */}
                        <Form.Row>
                            <Form.Group as={Col} className="form-group required " md={6} >
                                <Form.Label className="control-label">Overview</Form.Label>
                                <Form.Control placeholder="Overview" name="overview" onChange={() => onFileChange()} />
                            </Form.Group>
                        </Form.Row>
                        <Form.Row>
                            <Form.Group as={Col} className="form-group required " md={6} >
                            <Form.Label className="control-label">Content</Form.Label>
                            <textarea className="form-control" rows="5" placeholder="Content" name="content" 
                            onChange={(e) => onChangeInput(e)}></textarea>

                            </Form.Group>
                        </Form.Row>
                        <Col md={{ span: 8, offset: 2 }}>
                            <Button as={Link} to="/Timeline" variant="btn btn-outline-dark" md={6} onClick={(e) => onSubmit(e)}>
                                Post
                    </Button>
                        </Col>
                    </Col>
                </Row>
            </Form>
        </div>
        )
    }
}
