import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Nave from "./Component/Navebar/Nave";
import Home from "./Component/Home"
import About from "./Component/About"
import CreatePost from "./Component/CreatePost"

import { Switch , Route, Link, BrowserRouter as Router } from "react-router";

import Register from "./Component/Register";
import Login from "./Component/Login"
import axios from "axios";
import { decode } from "jsonwebtoken";
import PrivateRoute from "./PrivateRoute";
import { Alert } from "react-bootstrap";

export default class App extends Component {
  state = {
    user: null,
    isAuth: false,
    message: null,
  };

  loginHandler = async (cred) => {
    let { email, password } = cred;
    try {
      let data = await axios.post("http://localhost:5100/api/auth/login", {
        email,
        password,
      });
      console.log("login:", data);
      localStorage.setItem("token", data.data.token);

      this.getProfile(data.data.token);
    } catch (err) {
      this.setState({
        user: null,
        isAuth: false,
        message: err.response.data.message,
      });
    }
  };

  getProfile = async (token) => {
    try {
      let data = await axios.get("http://localhost:5100/api/auth/user", {
        headers: { "x-auth-token": token },
      });

      console.log("getProfile", data);
      this.setState({
        isAuth: true,
        user: data.data.user,
        message: null,
      });
    } catch (err) {
      this.setState({
        user: null,
        isAuth: false,
        message: err.response.data.message,
      });
    }
  };

  registerHandler = async (cred) => {
    try {
      let data = await axios.post(
        "http://localhost:3001/api/auth/register",
        cred
      );
      localStorage.setItem("token", data.data.token);
      this.getProfile(data.data.token);
    } catch (err) {
      console.log(err);
      this.setState({
        user: null,
        isAuth: false,
        message: err.response.data.message,
      });
    }
  };

  onLogoutHandler = (e) => {
    e.preventDefault();
    localStorage.removeItem("token");
    this.setState({
      isAuth: false,
      user: null,
    });
  };

  componentDidMount() {
    let token = localStorage.getItem("token");
    if (!(token == null)) {
      let user = decode(token);

      if (!user) {
        localStorage.removeItem("token");
      }

      this.getProfile(token);
    }
  }

  render() {
    const { isAuth, user } = this.state;

    const errMessage = this.state.message ? (
      <Alert variant="danger">{this.state.message}</Alert>
    ) : null;

    return (
      <div>

      </div>
    );
  }
}
