import React, { Component } from "react";
import { Form, Container, Button } from "react-bootstrap";

export default class Login extends Component {
  state = {};

  loginHandler = () => {
    //props from Login in App.js
    //this.state reps cred in App.js
    this.props.login(this.state);
  };

  changeHandler = (e) => {
    // console.log("name of field", e.target["name"]);
    // console.log("value of field", e.target.value);
    let temp = { ...this.state }; //copy state object
    temp[e.target.name] = e.target.value;
    this.setState(temp);
  };
  render() {
    return (
      <div>
            <Form >
                <Row className="justify-content-center mt-5">
                    <Col md={{ span: 6, offset: 3 }}>

                        <Form.Row><br /></Form.Row>
                        <Form.Row>
                            <Col className="form-group required" md={6}>
                                <Form.Label className="control-label">Last Name</Form.Label>
                                <Form.Control placeholder="Last name" name="lastName" />
                            </Col>

                        </Form.Row>
                        <Form.Row> <br /></Form.Row>
                        <Form.Row>
                            <Form.Group as={Col} md={6} controlId="formGridEmail" className="form-group required">
                                <Form.Label className="control-label">Email</Form.Label>
                                <Form.Control type="email" placeholder="Enter email" name="email" />
                            </Form.Group>
                        </Form.Row>
                        <Form.Row>
                            <Form.Group as={Col} md={6} controlId="formGridPassword" className="form-group required">
                                <Form.Label className="control-label">Password</Form.Label>
                                <Form.Control type="password" placeholder="Password" name="password" />
                            </Form.Group>
                        </Form.Row>
                        <Col md={{ span: 8, offset: 2 }}>
                            <Button variant="btn btn-outline-dark" md={6}>
                                Register
                    </Button>
                        </Col>
                    </Col>
                </Row>
            </Form>
        </div>
    );
  }
}
