import React, { useState, useEffect } from 'react'
import { Row, Form, Col, Button, Alert } from 'react-bootstrap'
import Axios from 'axios'
import '../App.css';
// import Axios from 'axios'

const CreatePost = (props) => {
    const [user, setUser] = useState({})// user info
    const [postcreate , setCreatePost] = useState(false) // to show aleart

    //to add the input inside user
    let onChangeInput = ({ target: { name, value } }) => {
        setUser({ ...user, [name]: value })
    }
    // to add the user info to database
    let onSubmit = (e) => {
        e.preventDefault()
        Axios.post('http://localhost:5500/api/user/Timeline', user)
            .then(res => {
                    if (res.data.PostCreatePost){
                        console.log(res.data)
                        props.history.push('/Timeline')
               }else{
                        setCreatePost(true)
                        setTimeout(() => {
                            setCreatePost(false)
                        }, 4000);
                    } 
            })
            .catch(err => console.log(err))
    }

    return (
        <div>
            <Form >
                <Row className="justify-content-center mt-5">
                    <Col md={{ span: 6, offset: 3 }}>
                        <Form.Row>
                            <Col className="form-group required " md={6}>
                                <Form.Label className="control-label">Title</Form.Label>
                                <Form.Control placeholder="Title" name="title" onChange={(e) => onChangeInput(e)}  />
                            </Col>
                        </Form.Row>
                        {/* <Form.Row><br /></Form.Row> */}
                        <Form.Row>
                            <Col className="form-group required " md={6}>
                                <Form.Label className="control-label">Poster</Form.Label>
                                <Form.Control placeholder="Poster" name="poster" onChange={(e) => onChangeInput(e)} />
                            </Col>

                        </Form.Row>
                        {/* <Form.Row><br /></Form.Row> */}
                        <Form.Row>
                            <Col className="form-group required " md={6}>
                                <Form.Label className="control-label">Image</Form.Label>
                                <Form.Control type="file" placeholder="image" name="image" onChange={(e) => onChangeInput(e)} />
                            </Col>

                        </Form.Row>
                        {/* <Form.Row> <br /></Form.Row> */}
                        <Form.Row>
                            <Form.Group as={Col} className="form-group required " md={6} >
                                <Form.Label className="control-label">Overview</Form.Label>
                                <Form.Control placeholder="Overview" name="overview" onChange={(e) => onChangeInput(e)} />
                            </Form.Group>
                        </Form.Row>
                        <Form.Row>
                            <Form.Group as={Col} className="form-group required " md={6} >
                            <Form.Label className="control-label">Content</Form.Label>
                            <textarea className="form-control" rows="5" placeholder="Content" name="content" 
                            onChange={(e) => onChangeInput(e)}></textarea>

                            </Form.Group>
                        </Form.Row>
                        <Col md={{ span: 8, offset: 2 }}>
                            <Button variant="btn btn-outline-dark" md={6} onClick={(e) => onSubmit(e)}>
                                Post
                    </Button>
                        </Col>
                    </Col>
                </Row>
            </Form>
        </div>
    )
}


export default CreatePost;
