import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import Nave from './Component/Navebar/Nave';
import Home from './Component/Home';
import Register from './Component/Register';
import Forget from './Component/Forget'
import Message from './Component/Message'
import About from './Component/About'
import {Switch , Route, Redirect , BrowserRouter as Router} from 'react-router-dom'
import './App.css';
import Login from './Component/Login';
const URL = "http://localhost:3001/api";

// import jwt_decode from 'jwt-decode'

export default class App extends Component {

  state = {
    isAuth: false,
    user: null,
    message: null,
    userid: ""
  };

  loginHandler = async (cred) => {
    try {
      let data = await axios.post(`${URL}/auth/login`, cred);
      // console.log(data.data.token);

      localStorage.setItem("token", data.data.token);

      this.getUser(data.data.token);
    } catch (err) {
      console.log(err);
      this.setState({
        isAuth: false,
        message: err.response.data.message,
        user: null,
      });
    }
  };

  logoutHandler = (e) => {
    e.preventDefault();
    localStorage.removeItem("token");
    this.setState({
      isAuth: false,
      user: null,
      message: null,
    });
  };
    render() {
  // console.log(this.state.user)



      return (
        <Router>
          <Nave isLogin ={this.state.isLogin} userLogin = {this.userLogin}/>
       <Switch>
         <Route path='/Home' component ={Home}/>
         <Route path='/Register' component ={Register}/>
         <Route path='/Login' render ={ (props) => <Login  {...props} userLogin = {this.userLogin} />}/>
         <Route path='/Forget' component ={Forget}/>
         <Route path='/Message' component ={Message}/>


         {this.state.isLogin ?<>  <Route path='/About' component ={About}/>  </> 
      :
      <> <Redirect to="/login" /> </>

      }  
       </Switch>
      </Router>
      )
    }
  }
