import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import Nave from './Component/Navebar/Nave';
import Home from './Component/Home';
import Register from './Component/Register';
import Forget from './Component/Forget'
import Message from './Component/Message'
import PrivateRoute from "./PrivateRoute";
import { decode } from "jsonwebtoken";
import { Alert } from "react-bootstrap";
import axios from "axios";
import About from './Component/About'
import { Switch, Route, Redirect, BrowserRouter as Router } from 'react-router-dom'
import './App.css';
import Login from './Component/Login';
const URL = "http://localhost:3001/api";

// import jwt_decode from 'jwt-decode'

export default class App extends Component {

  state = {
    isAuth: false,
    user: null,
    message: null,
    userid: ""
  };

  loginHandler = async (cred) => {
    try {
      let data = await axios.post(`${URL}/auth/login`, cred);
      // console.log(data.data.token);

      localStorage.setItem("token", data.data.token);

      this.getUser(data.data.token);
    } catch (err) {
      console.log(err);
      this.setState({
        isAuth: false,
        message: err.response.data.message,
        user: null,
      });
    }
  };

  logoutHandler = (e) => {
    e.preventDefault();
    localStorage.removeItem("token");
    this.setState({
      isAuth: false,
      user: null,
      message: null,
    });
  };
  getUser = async (token) => {
    try {
      let data = await axios.get(`${URL}/auth/user`, {
        headers: {
          "x-auth-token": token,
        },
      });
      //if success set state to isAuth and
      // populate data with user data
      this.setState({
        isAuth: true,
        message: null,
        user: data.data.user,
      });
    } catch (err) {
      console.log(err);
      localStorage.removeItem("token");
      this.setState({
        isAuth: false,
        message: err.response.data.message,
        user: null,
      });
    }
  };
  componentDidMount() {
    //gets token from localstorage
    let token = localStorage.getItem("token");
    //check if token exists
    if (!(token == null)) {
      //decodes token
      let user = decode(token);

      //check it token is valid
      if (!user) {
        //remove token
        localStorage.removeItem("token");
      }
      //retrieves updated user from token
      this.getUser(token);
    }
  }
  render() {
    // console.log(this.state.user)
    const { isAuth, message, user } = this.state;

    //used to display error message from API
    const errorMessage = message ? (
      <Alert variant="danger">{message}</Alert>
    ) : null;
    return (
    <div>
        <Nave user={user} logout={this.logoutHandler} />

        {errorMessage}
        <Switch>
          <Route path="/home" render={() => <Home name={"yasir"} />} />
          <PrivateRoute
            exact
            path="/register"
            isAuth={isAuth}
            component={Register}
          />
          <Route
            path="/login"
            render={() =>
              isAuth ? <About/> : <Login login={this.loginHandler} />
            }
          />
        </Switch>
      </div>



    )
  }
}
