import React, { useState, useEffect } from 'react'
import { Form, Button, Col, Row, Container } from 'react-bootstrap'
import Axios from 'axios'
import { Link } from 'react-router-dom'



const divStyle = {
  marginLeft: '90px',

};
export const Login = (props) => {
  const [login, setLogin] = useState({})


  let onChangeInput = ({ target: { name, value } }) => {
    setLogin({ ...login, [name]: value })
  }
  useEffect(() => {
    console.log(login)
  })

  let onSubmit = (e) => {
    e.preventDefault()
    Axios.post('http://localhost:4000/user/login', login)
      .then(res => {
        if (res.data.token) {

          localStorage.setItem('token', res.data.token)
          props.userLogin()
          props.history.push('/home')
        } else {

          console.log("email or password not correct")
        }



      })
      .catch(err => console.log(err))
  }
  return (
    <>
      <Form >
        <Row className="justify-content-center mt-5">
          <Col md={8}>
            <Form.Row>
              <Form.Group as={Col} controlId="formGridEmail">
                <Form.Label>Email</Form.Label>
                <Form.Control type="email" placeholder="Enter email" name="email" onChange={(e) => onChangeInput(e)} />
              </Form.Group>

              <Form.Group as={Col} controlId="formGridPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control type="password" placeholder="Password" name="password" onChange={(e) => onChangeInput(e)} />
              </Form.Group>
            </Form.Row>
            <Button variant="primary" type="submit" onClick={(e) => onSubmit(e)}>
              Submit
                    </Button>
            <Col md={{ span: 4, offset: 2 }} style={divStyle} >
              <Button as={Link} to="/Forget" variant="btn btn-outline-dark" md={6}>
                Forget Password?
</Button>
            </Col>
          </Col>
        </Row>
      </Form>



    </>
  )
}
