import React, { Component } from 'react'
import { Col, Image, Button } from 'react-bootstrap'
import { Link , withRouter } from 'react-router-dom'
import {MDBIcon} from "mdbreact"

 class PostCard extends Component {
    render() {
        // let {firstname, lastname} = this.props.user
        let { title, poster, image, overview, content, _id} = this.props.post
        return (
            <Col md={5} className="m-2" style={{ borderStyle: "double", borderRadius: "20px", display: "flex", flexDirection: "column", alignItems: "center" }}>
                <Image roundedCircle style={{ height: 150, width: 250 }} src={image} ></Image>
                {/* <h5>{firstname} {lastname}</h5> */}
                <p>{title}</p>
                <h5>{poster}</h5>
                <h5>{overview}</h5>
                <h4>{content} </h4>
                <Button as={Link} to={`/Timeline/${_id}`} className="mr-auto mb-3 d-inline-block" variant="outline-dark">Comments</Button>
                <MDBIcon icon="heart" size="3x" className="indigo-text pr-3" />
            </Col>
        )
    }
}

export default withRouter(PostCard);
