import React, { Component } from 'react'
import { Col, Image, Button, Form } from 'react-bootstrap'
import { Link, withRouter } from 'react-router-dom'
import { BsFillHeartFill, BsFillForwardFill } from "react-icons/bs";
import Axios from 'axios'
const forget = {
    fontSize: "25px"


};
const notForget = {
    marginLeft: "0",
    fontSize: "25px"
};
class PostCard extends Component {

    state = {};
    //http://localhost:5510/api/user/forget

    onSubmit = (e) => {
        e.preventDefault()
        Axios.post('http://localhost:5000/api/user/favbook', this.props._id)
            .then(res => {
                console.log(res)
                if (res.data) {
                    console.log("it is added")

                } else {
                    console.log("it is not added!!")
                }
            })
            .catch(err => console.log(err))
    }

    render() {
        // let {firstname, lastname} = this.props.user
        let { title, poster, image, overview, content, _id } = this.props.post


        return (
            <Col md={5} className="m-2" style={{ borderStyle: "double", borderRadius: "20px", display: "flex", flexDirection: "column", alignItems: "center" }}>
                <Image roundedCircle style={{ height: 150, width: 250 }} src={image} ></Image>
                {/* <h5>{firstname} {lastname}</h5> */}
                <img src={image}/>
                <p>{title}</p>
                <h5>{poster}</h5>
                <h5>{overview}</h5>

                <Form >
                    <Button style={notForget} as={Link} to="/Favorite" variant="btn-outline-dark" md={6}>
                        <p><BsFillHeartFill /></p>
                    </Button>

                </Form>



            </Col>
        )
    }
}

export default withRouter(PostCard);
