import React, { Component } from 'react'
import { Col, Image, Button, Form } from 'react-bootstrap'
import { Link, withRouter } from 'react-router-dom'

import { BsFillHeartFill, BsFillTrashFill , BsChatSquareDotsFill} from "react-icons/bs";
import Logo from '../marsLogo.png'



import Axios from 'axios'
const forget = {
    fontSize: "25px"


};
const notForget = {
    marginLeft: "0",
    fontSize: "25px"
};
class PostCard extends Component {



    DeletePost = () => {
        Axios.delete(`http://localhost:5000/api/post/delete/${this.props.post._id}`)
            .then(res => {
                console.log(res)
            })

    }
    render() {
        // let {firstname, lastname} = this.props.user

        let { title, poster, image, overview, content, _id } = this.props.post

        return (
            <Col md={5} className="m-2" style={{ borderStyle: "double", borderRadius: "20px", display: "flex", flexDirection: "column", alignItems: "center" }}>
                <Image roundedCircle style={{ height: 190, width: 250 }} src={`http://localhost:5000/${image}`} ></Image>
                {/* <h5>{firstname} {lastname}</h5> */}

                <p>{title}</p>
                <h5>{poster}</h5>
                <h5>{overview}</h5>
                <h5>{content}</h5>

                <Form >
                    <Button style={notForget} as={Link} to="/Favorite" variant="btn-outline-dark" md={6}>
                        <p><BsFillHeartFill /></p>
                    </Button>
                    <Button style={forget} as={Link} to={`/Comment/${_id}`} variant="btn-outline-dark" md={6}
                    >
                        <p><BsChatSquareDotsFill /></p>
                    </Button>
                    <Button as={Link} to={'/Timeline'} style={notForget} variant="btn-outline-dark" md={6} onClick={() => this.DeletePost()}>
                        <p><BsFillTrashFill /></p>
                    </Button>

                </Form>



            </Col>
        )
    }

}

export default withRouter(PostCard);
